mapper.insert:
insert into video_post ( title
                       , video_id
                       , thumbnail_file_id
                       , content
                       , created_at
                       , updated_at)
values ( #{title}
       , #{videoId}
       , #{thumbnailFileId}
       , #{content}
       , #{createdAt}
       , #{updatedAt})

mapper.update:
update video_post
set title             = #{title}
  , video_id          = #{videoId}
  , thumbnail_file_id = #{thumbnailFileId}
  , content           = #{content}
  , updated_at        = #{updatedAt}
where id = #{id}
